pair A, B, C, M, N, P;

M = (-1,0);
N = (1,0);
C = (0,0);
A = dir(140);
B = dir(20);
P = extension(A, A + rotate(10)*(C - A), B, B + rotate(10)*(C - B));

draw(M--N);
draw(arc(C,1,0,180));
draw(A--C--B);
draw(A--P--B);
