label("1",(1.5,0),S);
dot((0,0));
dot((-1,0));
dot((1,0));
draw((-2,-0.1)--(-2,-0.4));
draw((-1,-0.1)--(-1,-0.4));
draw((2,-0.1)--(2,-0.4));
draw((1,-0.1)--(1,-0.4));
