pair A, B, C, D, E, F, X, Y, Z;

A = (r,0);
B = A + (1,0);
C = B + r*dir(60);
D = C + dir(120);
E = D + (-r,0);
F = E + dir(240);
X = (0,0);
Y = B + (r,0);
Z = D + r*dir(120);

draw(A--B--C--D--E--F--cycle);
draw(A--C--E--cycle);
draw(F--X--A,dashed);
draw(B--Y--C,dashed);
draw(D--Z--E,dashed);

