P = (k/Cos(t/2),0,0);
Q = k/(k + 1)*A + k/(k + 1)*B;

draw(O--A,Arrow3(6));
draw(O--B,Arrow3(6));
draw(O--C,Arrow3(6));
draw(O--P,Arrow3(6));
draw(C--P,dashed);

